(function() {
  var c = createBlock('api-header', []);

  c.show('show', function ( $scope, $element, attrs ) {
    $scope.slugify = function(slug){
      slug = slug.replace(/(\s|-)+/g, '-')
      slug = slug.replace(/[^-_a-zA-Z0-9]/g, '')
      slug = slug.substr(0, 50);
      slug = slug.toLowerCase();
      return slug;
    };
  });

  c.edit('edit', function ( $scope, $element, attrs ) {
    if(!$scope.data) {
      $scope.data = {};
    }
  });

})();

(function() {
  var edit = ['ui.codemirror'];
  var c = createBlock('code', !is_hub || is_hub_edit ? edit : []);

  var statusCodes = function(code) {
    var codes= {
      '2XX': ['2XX', 'Success', 'success', true],
      200: [200, 'OK', 'success'],
      201: [201, 'Created', 'success'],
      202: [202, 'Accepted', 'success'],
      203: [203, 'Non-Authoritative Information', 'success'],
      204: [204, 'No Content', 'success'],
      205: [205, 'Reset Content', 'success'],
      206: [206, 'Partial Content', 'success'],
      300: [300, 'Multiple Choices', 'success'],
      301: [301, 'Moved Permanently', 'success'],
      302: [302, 'Found', 'success'],
      303: [303, 'See Other', 'success'],
      304: [304, 'Not Modified', 'success'],
      305: [305, 'Use Proxy', 'success'],
      306: [306, 'Unused', 'success'],
      307: [307, 'Temporary Redirect', 'success'],
      308: [308, 'Permanent Redirect', 'success'],

      '4XX': ['4XX', 'Error', 'error', true],
      400: [400, 'Bad Request', 'error'],
      401: [401, 'Unauthorized', 'error'],
      402: [402, 'Payment Required', 'error'],
      403: [403, 'Forbidden', 'error'],
      404: [404, 'Not Found', 'error'],
      405: [405, 'Method Not Allowed', 'error'],
      406: [406, 'Not Acceptable', 'error'],
      407: [407, 'Proxy Authentication Required', 'error'],
      408: [408, 'Request Timeout', 'error'],
      409: [409, 'Conflict', 'error'],
      410: [410, 'Gone', 'error'],
      411: [411, 'Length Required', 'error'],
      412: [412, 'Precondition Required', 'error'],
      413: [413, 'Request Entry Too Large', 'error'],
      414: [414, 'Request-URI Too Long', 'error'],
      415: [415, 'Unsupported Media Type', 'error'],
      416: [416, 'Requested Range Not Satisfiable', 'error'],
      417: [417, 'Expectation Failed', 'error'],
      418: [418, "I'm a teapot", 'error'],
      420: [420, "Enhance Your Calm", 'error'],
      422: [422, "Unprocessable Entity", 'error'],
      428: [428, 'Precondition Required', 'error'],
      429: [429, 'Too Many Requests', 'error'],
      431: [431, 'Request Header Fields Too Large', 'error'],
      500: [500, 'Internal Server Error', 'error'],
      501: [501, 'Not Implemented', 'error'],
      502: [502, 'Bad Gateway', 'error'],
      503: [503, 'Service Unavailable', 'error'],
      504: [504, 'Gateway Timeout', 'error'],
      505: [505, 'HTTP Version Not Supported', 'error'],
      511: [511, 'Network Authentication Required', 'error'],
    };
    if(code) return codes[code];
    return _.values(codes);
  };


  var code_types = [
    ['text', 'Text'],
    ['asp', 'ASP'],
    ['c', 'C'],
    ['coffeescript', 'CoffeeScript'],
    ['clojure', 'Clojure'],
    ['cplusplus', 'C++'],
    ['csharp', 'C#'],
    ['css', 'CSS'],
    ['curl', 'cURL'],
    ['d', 'D'],
    ['erlang', 'Erlang'],
    ['go', 'Go'],
    ['groovy', 'Groovy'],
    ['java', 'Java'],
    ['javascript', 'JavaScript'],
    ['jinja2', 'Jinja2'],
    ['json', 'JSON'],
    ['julia', 'Julia'],
    ['haxe', 'Haxe'],
    ['haml', 'HAML'],
    ['html', 'HTML'],
    ['http', 'HTTP'],
    ['less', 'LESS'],
    ['lua', 'Lua'],
    ['markdown', 'Markdown'],
    ['mysql', 'MySQL'],
    ['objectivec', 'Objective-C'],
    ['php', 'PHP'],
    ['pgsql', 'Postgres'],
    ['python', 'Python'],
    ['r', 'R'],
    ['ruby', 'Ruby'],
    ['sass', 'SASS'],
    ['scala', 'Scala'],
    ['shell', 'Shell'],
    ['stylus', 'Stylus'],
    ['xml', 'XML'],
    ['yaml', 'YAML'],
  ];
  var code_types_obj =  _.object(code_types);

  c.show('show', function ( $scope, $element, attrs, ngModel ) {
    $scope.code_types = code_types;
    $scope.code_types_obj = code_types_obj;
    $scope.current = 0;

    if(attrs.status) {
      $scope.status = true;
      $scope.statusCodes = statusCodes();
      $scope.getStatusCode = statusCodes;
    }

    if($element.attr('scheme') === 'dark' || $element.closest('.docs-right').length) {
      $scope.theme = 'cm-s-lockitron';
    } else {
      $scope.theme = 'cm-s-neo';
    }

    setTimeout(function() {
      $element.find('pre').each(function() {
        var $el = $(this);
        CodeMirror.setMode($el.data('mode'), function(mode) {
          CodeMirror.runMode($el.text(), mode, $el[0], {tabSize: 2});
        });

      });
    }, 0);

    $scope.choose = function(i) {
      $scope.current = i;
    };
  });

  function fromGistToAce(type) {
    type = type.toLowerCase();
    
    // Swap out if we call it something different
    if(type == "shell") {
      type = "sh";
    }

    // Check if it's in the list
    var found = false;
    $.each(code_types, function(k, v) { 
      if(v[0] == type) {
        found = true;
      }
    });
    if(!found) {
      type = 'text';
    }

    return type;
  }

  c.edit('edit', function ( $scope, $element, attrs, ngModel) {
    /*
     * TODO:
     * [ ] Add more languages
     *
     * [ ] Fix the load from GitHub stuff to work for multiple
     * [ ] Clean up the Add link
     */

    if(attrs.status) {
      $scope.status = true;
      $scope.statusCodes = statusCodes();
      $scope.getStatusCode = statusCodes;
    }
    $scope.code_types = code_types;
    $scope.code_types_obj = code_types_obj;

    //TODO: Maybe allow a "default" to be defined for blocks?
    // Or better yet, maybe an "on create" function?
    if(!$scope.data['codes']) {
      var is_new = true;
      $scope.data['codes'] = [
        {code: '', language: 'text'}
      ];
    }

    $scope.choose = function(i) {
      $scope.current = i;
      setTimeout(function() {
        editors[i].refresh();
      }, 1);
    };

    $scope.delete = function(i) {
      $scope.data['codes'].splice(i, 1);
      if(!$scope.data['codes'].length) {
        $scope.data['codes'] = [
          {code: '', language: 'text'}
        ];
      }
      if($scope.current >= $scope.data['codes'].length) {
        $scope.current = $scope.data['codes'].length - 1;
      }

      ngModel.$setViewValue($scope.data);
    };

    $scope.rename = function(i) {
      $scope.data['codes'][i].name = prompt("What shall this file be named?", $scope.data['codes'][i].name);
      ngModel.$setViewValue($scope.data);
    };

    $scope.add = function() {
      is_new = true;
      $scope.data['codes'].push({
        code: '',
        language: 'text',
      });

      $scope.current = $scope.data['codes'].length - 1;
      ngModel.$setViewValue($scope.data);

      createEditor($scope.data['codes'][$scope.current], $scope.current);
    };

    $scope.current = 0;

    var editors = [];
    $scope.editorOptions = [];

    function createEditor(code, i) {
      $scope.editorOptions.push({
        lineNumbers: true,
        viewportMargin: Infinity,
        theme: "neo",
        tabSize: 2,
        lineWrapping: true, // otherwise, breaks scrolling
        mode: "",
        onLoad: function(editor) {
          editors[i] = editor;
          setTimeout(function() {
            CodeMirror.setMode(code['language'], editors[i]);
          }, 100);
        },
      });
    };

    /* Initialize codemirror for existing ones */
    _.each($scope.data['codes'], createEditor);

    $scope.changeMode = function(i) {
      CodeMirror.setMode($scope.data.codes[i].language, editors[i]);
    };

    $scope.import = function() {
      var url = prompt("Please enter the embed URL from GitHub");
      if(!url) return;


      var gist = url.match(/(?:gist.github.com\/(?:.[a-zA-Z0-9]+\/)?([a-zA-Z0-9]*)(?:\.git|\.js)?)/);
      if(!gist) {
        alert("This doesn't seem to be a proper gist.");
        return;
      }

      $.getJSON("https://api.github.com/gists/" + gist[1], function(data) {
        var files = data.files;
        var keys = Object.keys(files);
        var file = files[keys[0]]

        $scope.data.codes[0].code = file.content;
        $scope.data.codes[0].language = fromGistToAce(file.language);
        $scope.data.codes[0].gist = gist[1];

        ngModel.$setViewValue($scope.data);

        $scope.changeMode();
        $scope.$apply();
      });
    };
  });

})();

