(function() {
  var c = createBlock('parameters', []);

  c.show('show', function ( $scope, $element, attrs ) {
    $scope.getNumber = function(num) {
      return new Array(num);   
    }
  });

  c.edit('edit', function ( $scope, $element, attrs ) {
    if(!$scope.data.data) {
      $scope.data.data = {};
    }
    if(!$scope.data.cols) {
      $scope.data.cols = 3;
    }
    if(!$scope.data.rows) {
      $scope.data.rows = 0;
    }
    $scope.getNumber = function(num) {
      return new Array(num);   
    }

    var $tc = $('.table-control', $element);
    $scope.row = 0;
    $scope.hover = function(i) {
      $scope.row = i;
      var $tr = $('table tr', $element).eq(i + 1);
      $tc.css({
        'top': $tr.position().top,
        'height': $tr.height()
      });
    };

    $scope.addRow = function(addInPlace, dontFocus) {
      if(addInPlace) {
        var new_rows = {};
        _.each($scope.data.data, function(v, k) {
          var k_split = k.split('-');
          var current_row = parseInt(k_split[0], 10);
          if(current_row > $scope.row) {
            k_split[0] = current_row + 1;
          }
          new_rows[k_split.join('-')] = v;
        });
        $scope.data.data = new_rows;
      } else {
        $scope.row = $scope.data.rows-1; // For focusing below
      }

      if(!dontFocus) {
        $scope.focus[($scope.row+1) + '-' + 0] = true;
      }
      $scope.data.rows++;
    };

    $scope.addCol = function() {
      $scope.focus['0-' + $scope.data.cols] = true;
      $scope.data.cols++;
    }

    $scope.removeRow = function() {
      var confirmed = confirm('Are you sure you want to delete this row?');
      if(!confirmed) return;

      var new_rows = {};
      _.each($scope.data.data, function(v, k) {
        var k_split = k.split('-');
        var current_row = parseInt(k_split[0], 10);
        if(current_row > $scope.row) {
          k_split[0] = current_row - 1;
        }
        if(current_row !== $scope.row) {
          new_rows[k_split.join('-')] = v;
        }
      });
      $scope.data.data = new_rows;
      $scope.data.rows--;
      if($scope.data.rows <= 0) $scope.data.rows = 1;
    };

    $scope.dummy = {};
    $scope.$watch('dummy', function(newVal) {
      var add = false;
      _.each($scope.dummy, function(val, col) {
        $scope.data.data[$scope.data.rows + '-' + col] = val;
        if(val) {
          add = true;
          $scope.focus[$scope.data.rows + '-' + col] = true;
          delete $scope.dummy[col];
        }
      })
      if(add) {
        $scope.data.rows++;
      }
    }, true);

    $scope.focus = []; // Focus on the newest row!

  });

})();

