(function() {
  var c = createBlock('embed', []);

  c.show('show', function ( $scope, $element, attrs ) {
    setTimeout(function() {
      $element.find('.html-replace').html($scope.data.html);
      $element.find('.iframe-replace').html('<iframe class="media-iframe" src="'+$scope.data.url+'"></iframe>');
    }, 10);
  });

  var specialDomains = {
    'gist.github.com': function(data, next) {

      var gist = data.url.match(/(?:gist.github.com\/(?:.[a-zA-Z0-9]+\/)?([a-zA-Z0-9]*)(?:\.git|\.js)?)/);

      $.getJSON("https://api.github.com/gists/" + gist[1], function(data) {
        var files = data.files;
        var keys = Object.keys(files);
        var file = files[keys[0]]

        next("<pre><code>"+$('<div/>').text(file.content).html()+"</code></pre>");

      });
    },

    'codepen.io': function(data, next) {
      var codepen = data.url.replace('/pen/', '/embed/');
      next("<iframe height='350' scrolling='no' src='" + codepen + "' frameborder='no' allowtransparency='true' allowfullscreen='true' style='width: 100%;'></iframe>");
    },

  };

  c.edit('edit', function ( $scope, $element, attrs ) {
    $scope.url = $scope.data.url;

    $scope.embed = function() {
      $scope.loading = true;
      $.embedly.extract($scope.url, {
        key: 'f2aa6fc3595946d0afc3d76cbbd25dc3',
      }).progress(function(data) {
        $scope.loading = false;
        var host = new URL($scope.url).hostname;

        var next = function(html) {
          $scope.data.html = html;
          $scope.data.url = data.url;
          $scope.data.title = data.title;
          $scope.data.favicon = data.favicon_url;
          if(data.images.length) {
            $scope.data.image = data.images[0].url;
          }
          $scope.$apply();
        };

        if(specialDomains[host]) {
          specialDomains[host](data, next);
        } else if(data.media.html) {
          next(data.media.html);
        } else {
          next(false);
        }
      });
    };

    $scope.clear = function() {
      $scope.data = {};
    }

    $("input", $element).on( "keypress", function(event) {
      if (event.which == 13) {
        event.preventDefault();
        $scope.embed();
      }
    });
  });

})();

